document.addEventListener("DOMContentLoaded",function(){var e=[].slice.call(document.querySelectorAll("img.lazy")),t=[].slice.call(document.querySelectorAll(".lazy-background")),n=[].slice.call(document.querySelectorAll("[data-bg]"));if("IntersectionObserver"in window){let c=new IntersectionObserver(function(e,t){e.forEach(function(e){if(e.isIntersecting){let t=e.target;t.src=t.dataset.src,t.srcset=t.dataset.srcset,t.classList.remove("lazy"),c.unobserve(t)}})});e.forEach(function(e){c.observe(e)});let r=new IntersectionObserver(function(e,t){e.forEach(function(e){e.isIntersecting&&(e.target.classList.add("visible"),r.unobserve(e.target))})});t.forEach(function(e){r.observe(e)});let s=new IntersectionObserver(function(e,t){e.forEach(function(e){if(e.isIntersecting){let t=e.target;t.style.backgroundImage="url("+t.dataset.bg+")",s.unobserve(t)}})});n.forEach(function(e){s.observe(e)})}else e.forEach(function(e){e.src=e.dataset.src,e.srcset=e.dataset.srcset}),t.forEach(function(e){e.classList.add("visible")}),n.forEach(function(e){e.style.backgroundImage="url("+e.dataset.bg+")"})}),document.addEventListener("DOMContentLoaded",function(){});


$(document).ready(function(){

	$('.buildman').height($('.about').height() - 109);

	$(".owl-port").owlCarousel({
		items: 2,
		loop: true,
		autoplay: true,
		margin: 50,
		autoplayTimeout: 3000
	});
	$(".obj-car").owlCarousel({
		items: 1,
		loop: true,
	});
	

	$('.feature').hover(function(){
		$(this).find('.sub-feature_desc').fadeIn();

	},function(){
		$(this).find('.sub-feature_desc').hide();
	});

	$(window).scroll(function(){
	      if ($(this).scrollTop() > 100) {
	          $('.call').fadeIn();	
	      } else {
	          $('.call').fadeOut();
	      }
	});
	$('.navbar').width($('.container').width());
	$('.call-button').click(function(){
		$('.call').animate({
			height: '100px'
		},300);
		$('.call-form input').fadeIn();
		$('.send-button').css('background','royalblue')
		$('.contact-button').hide();
		$('.send-button').fadeIn();
	})

});